document.addEventListener("DOMContentLoaded",()=>{let e=0;let o=1;document.getElementById("currentYear").innerText=(new Date).getFullYear();document.querySelectorAll(".nav-link").forEach(e=>{e.addEventListener("click",e=>{document.querySelectorAll(".nav-link").forEach(e=>e.classList.remove("active"));e.target.classList.add("active")})});window.addEventListener("scroll",()=>{o=window.scrollY>e?1:-1;if(window.scrollY>100){document.getElementById("pf_nav").classList.add("navbar-scrolled");document.getElementById("pf_nav").classList.remove("navbar-scroll")}else{document.getElementById("pf_nav").classList.add("navbar-scroll");document.getElementById("pf_nav").classList.remove("navbar-scrolled")}});document.getElementById("toggleShow").addEventListener("click",e=>{document.querySelectorAll(".pf_more").forEach(e=>{if(e.classList.contains("d-block")){e.classList.remove("d-block")}else{e.classList.add("d-block")}});e.target.innerText=e.target.innerText==="Show Less"?"Show More":"Show Less"});isElementVisibleInViewportCenter=e=>{const{top:o,bottom:n}=e.getBoundingClientRect();const t=window.innerHeight||document.documentElement.clientHeight;return(o>0||n>0)&&o<t}});